package os.zbwlkj.com.os.Act;import android.app.Instrumentation;import android.graphics.Color;import android.os.Bundle;import android.view.KeyEvent;import android.view.View;import android.view.Window;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.bumptech.glide.load.engine.DiskCacheStrategy;import okhttp3.internal.cache.CacheStrategy;import os.zbwlkj.com.os.Class.act_class;import os.zbwlkj.com.os.Class.action_class;import os.zbwlkj.com.os.Dialog.WaitDialog;import os.zbwlkj.com.os.R;import os.zbwlkj.com.os.Utils.Net;import os.zbwlkj.com.os.Utils.TextUnt;import os.zbwlkj.com.os.Utils.Utils;/** * 展示商品/票跟/干货/生鲜/水果/饮料/牛奶/零食的界面 * 用来驱动Fragment的管理器 */public class Act_showMain extends act_class {    @Override    protected void onCreate(Bundle savedInstanceState) {        getWindow().requestFeature(Window.FEATURE_CONTENT_TRANSITIONS);        setTransparentBar();        setInTransition(action_class.TRANSITION.SLIDE);/*设置进入动画 */        setOutTransition(action_class.TRANSITION.EXPLODE);/*设置退出动画*/        setContentView(R.layout.act_showmain);        init();        super.onCreate(savedInstanceState);    }    /**     * 系统初始化     */    private void init() {        /**         * 获取屏幕高度和宽度  用来获取一张正确的海报         */        int MetricHeight = getHeightPixels();        int MetricWidth = getWidthPixels();        ImageView img = findViewById(R.id.act_showmain_imgPhotos);        Glide.with(getApplicationContext()).load("http://120.79.63.36/OS_PHOTOS/123.png")                .diskCacheStrategy(DiskCacheStrategy.NONE).skipMemoryCache(false).into(img);        TextUnt.with(this, R.id.act_showmain_btnPay).setBackground(Utils.CreateDrawable                (getApplicationContext(), 1, R.color.color_accent, R.color.color_accent, 10));        listener();    }    /**     * 初始化监听事件     */    private void listener() {        /**         * 退出监听         */        setOnbackListener(R.id.act_showmain_btnBack, false);        /*初始化界面  设置导航的字号*/        TextUnt.with(this, R.id.act_showmain_btnHome).setTextSize(18).setOnClick(new View                .OnClickListener() {            @Override            public void onClick(View v) {                TextUnt.with(Act_showMain.this, R.id.act_showmain_btnHome).setTextSize(18)                        .setTextColor(Color.WHITE);                TextUnt.with(Act_showMain.this, R.id.act_showmain_btnMe).setTextSize(17)                        .setTextColor("#bfbfbf");            }        });        TextUnt.with(this, R.id.act_showmain_btnMe).setOnClick(new View.OnClickListener() {            @Override            public void onClick(View v) {                TextUnt.with(Act_showMain.this, R.id.act_showmain_btnMe).setTextSize(18)                        .setTextColor(Color.WHITE);                TextUnt.with(Act_showMain.this, R.id.act_showmain_btnHome).setTextSize(17)                        .setTextColor("#bfbfbf");            }        });    }}