package os.zbwlkj.com.os.Act.Shop;import android.os.Bundle;import android.transition.Visibility;import android.view.Gravity;import android.view.ViewGroup;import os.zbwlkj.com.os.Class.act_class;import os.zbwlkj.com.os.R;import os.zbwlkj.com.os.Utils.Utils;public class toPayshop extends act_class {    @Override    protected void onCreate(Bundle savedInstanceState) {        setTransparentBar();        setInTransitionWithSlide(500, Visibility.MODE_IN, Gravity.LEFT);        setOutTransitionWithSlide(500, Visibility.MODE_OUT, Gravity.RIGHT);        setContentView(R.layout.act_topayshop);        init();        super.onCreate(savedInstanceState);    }    /**     * 初始化     */    private void init() {        /**         * 设置地址的边框         */        findViewById(R.id.act_topayshopAddrbody).setBackground(Utils.CreateDrawable(1, "#ffffff",                "#ffffff", 10));        /**         * 设置商品的信息         */        findViewById(R.id.act_topayshopMsgBody).setBackground(Utils.CreateDrawable(1, "#ffffff",                "#ffffff", 10));    }}