package os.zbwlkj.com.os.Act.System;import android.annotation.SuppressLint;import android.os.Bundle;import android.transition.Visibility;import android.view.Gravity;import os.zbwlkj.com.os.Class.act_class;import os.zbwlkj.com.os.R;import os.zbwlkj.com.os.Utils.TextUnt;import os.zbwlkj.com.os.Utils.Utils;/** * 显示商家的地址和信息 */public class toSeebusiness extends act_class {    @Override    protected void onCreate(Bundle savedInstanceState) {        setTransparentBar();        setInTransitionWithExplode(1000, Visibility.MODE_IN);        setOutTransitionWithSlide(500, Visibility.MODE_OUT, Gravity.BOTTOM);        setContentView(R.layout.act_businessmsg);        init();        super.onCreate(savedInstanceState);    }    /**     * 系统初始化     */    @SuppressLint("ResourceType")    private void init() {        /**         * 设置两个按钮的边框样式         */        findViewById(R.id.act_businessmsgBtnCallbusiness).setBackground(Utils.CreateDrawable(1,                getResources().getString(R.color.SystemTheme), getResources().getString(R.color                        .SystemTheme), 10));        findViewById(R.id.act_businessmsgBtnloveBusiness).setBackground(Utils.CreateDrawable(1,                "#a6a6a6", "#a6a6a6", 10));        /*设置评分的边框*/        TextUnt.with(this, R.id.act_businessmsgScore).setBackground(Utils.CreateDrawable(1,                getResources().getString(R.color.SystemTheme), getResources().getString(R.color                        .SystemTheme), 10));    }}