package os.zbwlkj.com.os.Assembly;/** * 系统组件  添加Log到服务器的日志信息中 方便远程调试 */public class SystemLog {    public final static int MESSAGE_LOG = 0;/*平常的收集信息*/    public final static int MESSAGE_ERROR = 1;/*程序的错误信息*/    public final static int MESSAGE_DEADLY = 2;/*程序运行过程的致命信息*/    /**     * 初始化要提交的信息     */    public SystemLog(int _mode, String _log) {        upload(_mode, _log);    }    /**     * 开始提交错误信息     */    private void upload(int _mode, String _log) {        switch (_mode) {            case MESSAGE_LOG:                upload_log();                break;            case MESSAGE_ERROR:                upload_error();                break;            case MESSAGE_DEADLY:                upload_deadly();                break;        }    }    /**     * 提交给服务器一个日志信息     */    private void upload_log() {    }    /**     * 提交给服务器一个错误不致命的信息     */    private void upload_error() {    }    /**     * 提交给服务器一个致命的信息     */    private void upload_deadly() {    }}