package os.zbwlkj.com.os.Class;import android.view.animation.LinearInterpolator;/** * 震动插值器 */public class ShockInterpolator extends LinearInterpolator {    private float factor;    public ShockInterpolator() {        this.factor = 0.15f;    }    @Override    public float getInterpolation(float input) {        return (float) (Math.pow(2, -10 * input) * Math.sin((input - factor / 4) * (6 * Math.PI)                / factor) + 1);    }}