package os.zbwlkj.com.os.Fragment;import android.annotation.SuppressLint;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.HorizontalScrollView;import android.widget.LinearLayout;import java.util.ArrayList;import os.zbwlkj.com.os.R;import os.zbwlkj.com.os.Utils.TextUnt;import os.zbwlkj.com.os.Utils.Utils;/** * 我的中心 */public class Me extends Fragment {    private final int ME_TO_SHOP = 0;    private final int ME_TO_MOVIETICKET = 1;    private String MSG = "Me.java[+]";    private View item;    private LinearLayout ll_itembody;    private ArrayList<String> ar_selectTitle = new ArrayList<>();    private ArrayList<View> ar_selectItem = new ArrayList<>();    private int select_id = 0;/*设置该要显示那个界面*/    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        item = inflater.inflate(R.layout.frg_me, null, false);        ll_itembody = item.findViewById(R.id.frg_meItemBody);/*选择的BODY*/        ar_selectTitle.add("商品");        ar_selectTitle.add("电影票");        ar_selectTitle.add("兑换卷");        ar_selectTitle.add("抵用卷");        ar_selectTitle.add("外卖食品");        init();        listener();        return item;    }    /**     * 监听事件     */    private void listener() {    }    /**     * 界面初始化整理     */    @SuppressLint("ResourceType")    private void init() {        for (int i = 0; i < ar_selectTitle.size(); i++) {            View se_item = LayoutInflater.from(getContext()).inflate(R.layout.item_select, null);            ll_itembody.addView(se_item);            ar_selectItem.add(se_item);            if (i == 0) {                TextUnt.with(se_item, R.id.item_selectTitle).setBackground(Utils.CreateDrawable                        (1, getResources().getString(R.color.SystemSelectColor), getResources()                                .getString(R.color.SystemSelectColor), 50)).setTextColor                        (getResources().getString(R.color.SystemTextColor)).setText                        (ar_selectTitle.get(i));            } else {                /**                 * 设置没有点击的时候的文字的颜色                 */                TextUnt.with(se_item, R.id.item_selectTitle).setTextColor(getResources()                        .getString(R.color.SystemNotSelectColor)).setText(ar_selectTitle.get(i));            }            se_item.setTag(i);            se_item.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View v) {                    int position = (int) v.getTag();                    for (int i = 0; i < ar_selectItem.size(); i++) {                        TextUnt.with(ar_selectItem.get(i), R.id.item_selectTitle)                                .setTextColor(getResources().getString(R.color                                        .SystemNotSelectColor)).setText(ar_selectTitle.get(i)).setBackColor("#ffffff");                    }                    TextUnt.with(ar_selectItem.get(position), R.id.item_selectTitle).setBackground(Utils.CreateDrawable                            (1, getResources().getString(R.color.SystemSelectColor), getResources()                                    .getString(R.color.SystemSelectColor), 50)).setTextColor                            (getResources().getString(R.color.SystemTextColor)).setText                            (ar_selectTitle.get(position));                    select_id = position;                    toShowpage();                }            });        }    }    @Override    public void onResume() {        Log.i(MSG, "onResume");        super.onResume();    }    @Override    public void onPause() {        Log.i(MSG, "onPause");        super.onPause();    }    @Override    public void onStop() {        Log.i(MSG, "onStop");        super.onStop();    }    @Override    public void onStart() {        Log.i(MSG, "onStart");        super.onStart();    }    public void toShowpage() {        switch (select_id) {            case ME_TO_SHOP:                Log.i(MSG,"商品");                break;            case ME_TO_MOVIETICKET:                Log.i(MSG,"电影票");                break;        }    }}