package os.zbwlkj.com.os.Fragment;import android.annotation.SuppressLint;import android.content.Context;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.bigkoo.convenientbanner.ConvenientBanner;import com.bigkoo.convenientbanner.holder.CBViewHolderCreator;import com.bigkoo.convenientbanner.holder.Holder;import com.bigkoo.convenientbanner.listener.OnItemClickListener;import com.bumptech.glide.Glide;import com.bumptech.glide.load.engine.DiskCacheStrategy;import java.util.ArrayList;import os.zbwlkj.com.os.R;import os.zbwlkj.com.os.Utils.Utils;/** * 展示商品的界面 */public class Shop extends Fragment implements OnItemClickListener {    private String MSG = "Shop.java[+]";    private View item;    private TextView tv_price;    private ConvenientBanner cb_shop;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        item = inflater.inflate(R.layout.act_shop, null, false);        init();        return item;    }    /**     * 初始化 模拟获取图片     */    @SuppressLint("ResourceType")    private void init() {        ArrayList<String> img_url = new ArrayList();        img_url.add("http://120.79.63.36/OS_PHOTOS/123.png");        img_url.add("http://120.79.63.36/OS_PHOTOS/1234.png");        img_url.add("http://120.79.63.36/OS_PHOTOS/123.png");        img_url.add("http://120.79.63.36/OS_PHOTOS/1234.png");        cb_shop.setPages(new CBViewHolderCreator<ImageHolderView>() {            @Override            public ImageHolderView createHolder() {                return new ImageHolderView();            }        }, img_url).setPageIndicatorAlign(ConvenientBanner.PageIndicatorAlign.ALIGN_PARENT_RIGHT)                .setOnItemClickListener(this).startTurning(5500);        /**         * 执行数字增加动画         */        Utils.NumberAddAnimator(0.00f, 34.00f, tv_price);        listener();    }    /**     * 事件监听事件     */    private void listener() {    }    @Override    public void onItemClick(int position) {    }    public class ImageHolderView implements Holder<String> {        private ImageView img;        @Override        public View createView(Context context) {            img = new ImageView(context);            img.setAdjustViewBounds(true);            img.setScaleType(ImageView.ScaleType.FIT_XY);            return img;        }        @Override        public void UpdateUI(Context context, int position, String data) {            Glide.with(context).load(data).diskCacheStrategy(DiskCacheStrategy.NONE)                    .skipMemoryCache(false).into(img);        }    }}