package os.zbwlkj.com.os.Fragment;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.bumptech.glide.load.engine.DiskCacheStrategy;import os.zbwlkj.com.os.R;import os.zbwlkj.com.os.Utils.Utils;public class Shop extends Fragment {    private View item;    private ImageView iv_shopimg;    private TextView tv_price;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        item = inflater.inflate(R.layout.frg_shop, null, false);        iv_shopimg = item.findViewById(R.id.frg_shopImg);/*商品的图片*/        tv_price = item.findViewById(R.id.frg_shopPrice);/*商品的价格*/        init();        return item;    }    /**     * 初始化 模拟获取图片     */    private void init() {        /**         * 执行数字增加动画         */        Utils.NumberAddAnimator(0.00f, 34.00f, tv_price);        Glide.with(getContext()).load("http://120.79.63.36/OS_PHOTOS/123.png").diskCacheStrategy                (DiskCacheStrategy.NONE).skipMemoryCache(false).into(iv_shopimg);    }}