package os.zbwlkj.com.os.Utils;import android.animation.AnimatorSet;import android.animation.ValueAnimator;import android.view.View;import android.view.ViewGroup;import android.widget.Button;/** * 动画工厂 */public class animatorFactor {    private static ButtonValuesanimator buttonValuesanimator = new ButtonValuesanimator();    public static ButtonValuesanimator getButtonValuesanimator() {        return buttonValuesanimator;    }    /**     * 按钮的动画类 实现左右两边同时Margin实现圆形按钮     */    public static class ButtonValuesanimator {        /**         * @param _view         * @param _s         * @param _e         */        public void start(final View _view, float _s, float _e, int _time) {            AnimatorSet set = new AnimatorSet();            ValueAnimator animator = ValueAnimator.ofFloat(_s, _e);            animator.addUpdateListener(new ValueAnimator.AnimatorUpdateListener() {                @Override                public void onAnimationUpdate(ValueAnimator animation) {                    float value = (float) animation.getAnimatedValue();                    ViewGroup.MarginLayoutParams layoutParams = (ViewGroup.MarginLayoutParams)                            _view.getLayoutParams();                    layoutParams.leftMargin = (int) value;                    layoutParams.rightMargin = (int) value;                    _view.setLayoutParams(layoutParams);                }            });        }    }}