package os.zbwlkj.com.os.Views;import android.annotation.SuppressLint;import android.content.Context;import android.graphics.Typeface;import android.util.AttributeSet;import android.widget.TextView;@SuppressLint("AppCompatCustomView")public class SystemNumerView extends TextView {    public SystemNumerView(Context context) {        super(context);        Typeface typeface = Typeface.createFromAsset(getContext().getAssets(), "font/number" + "" +                ".ttf");        setTypeface(typeface);    }    public SystemNumerView(Context context, AttributeSet attrs) {        super(context, attrs);        Typeface typeface = Typeface.createFromAsset(getContext().getAssets(), "font/number" + "" +                ".ttf");        setTypeface(typeface);    }    public SystemNumerView(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        Typeface typeface = Typeface.createFromAsset(getContext().getAssets(), "font/number" + "" +                ".ttf");        setTypeface(typeface);    }}